SELECT `user`.id, `user`.username, `user`.dept_id, `user`.status, `user`.remark, `user`.nickname, `user`.sex, `user`.age, `user`.email, `user`.phone, role.id role_id, role.name role_name, `group`.id group_id, `group`.name group_name, `user`.dept_name FROM (SELECT , dept.name dept_name FROM sys_dept dept, sys_user `user`
LEFT JOIN sys_user_role ur ON `user`.id = ur.uid
LEFT JOIN sys_user_group ug ON `user`.id = ug.uid
WHERE `user`.del_flag = 0 AND `user`.dept_id = dept.id
AND dept.id = #{deptId}
AND ur.role_id = #{roleId}
AND ug.group_id = #{groupId}
AND `user`.username LIKE CONCAT('%', #{keyword}, '%')
AND `user`.status = #{status}
GROUP BY `user`.id ORDER BY `user`.id desc
LIMIT #{pageNum}, #{pageSize}
) `user` LEFT JOIN sys_user_role ur ON `user`.id = ur.uid LEFT JOIN sys_role role ON role.id = ur.role_id LEFT JOIN sys_user_group ug ON `user`.id = ug.uid LEFT JOIN sys_group `group` ON `group`.id = ug.group_id ORDER BY `user`.id desc

